var testswarm = require( "./lib/testswarm" ),
	testUrl = "http://localhost/testswarm-ui-checkout/tests/unit/",
	runs = {};

["accordion", "autocomplete"].forEach(function (suite) {
	runs[suite] = testUrl + suite + "/" + suite + ".html";
});

testswarm.createClient( {
	url: "http://localhost:82/testswarm/"
} )
.addReporter( testswarm.reporters.cli )
.auth( {
	id: "jqueryui",
	token: "b1252fbbcefaf9fdd6eef077d6b2f03cf3bbcb88"
} )
.addjob(
	{
		name: "jQuery UI commit",
		runs: runs,
		browserSets: ["popular"],
	}, function( err, passed ) {
		if ( err ) {
			throw err;
		}
		process.exit( passed ? 0 : 1 );
	}
);
